import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect, Link } from "react-router-dom";
import {
  SESSION_STORAGE_TOKEN,
  SESSION_STORAGE_USERNAME
} from "../constants/Constants";
import "../css/App.css";
import { SideMenu } from "./pageComponents/SideMenu";
import { RightMenu } from "./pageComponents/RightMenu";
import Welcome from "./pageComponents/Welcome";
import { Alert } from "reactstrap";
import _ from "lodash";
import { Modal } from "react-bootstrap";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      child: [],
      redirect: false,
      childFirstName: "",
      childLastName: "",
      childGender: "",
      childDob: ""
    };
  }

  logoutHandler = e => {
    sessionStorage.removeItem(SESSION_STORAGE_TOKEN);
    sessionStorage.removeItem(SESSION_STORAGE_USERNAME);
    this.setState({
      firstName: "",
      lastName: "",
      email: "",
      child: [],
      redirect: true,
      message:""
    });
  };
  getInitialState = () => {
    return { showModal: false };
  };

  closeHandler = () => {
    this.setState({ showModal: false });
  };

  openHandler = () => {
    this.setState({ showModal: true });
  };

  componentDidMount() {
    if (
      sessionStorage.getItem(SESSION_STORAGE_TOKEN) &&
      sessionStorage.getItem(SESSION_STORAGE_USERNAME)
    ) {
      const token = sessionStorage.getItem(SESSION_STORAGE_TOKEN);
      const data = sessionStorage.getItem(SESSION_STORAGE_USERNAME);

      console.log("username:", data);
      console.log("token", token);

      ApiService.getParentData(data).then(response => {
        this.setState({
          firstName: response.data.firstName,
          lastName: response.data.lastName,
          email: response.data.email,
          child: response.data.child
        });
      });
    } else {
      this.setState({ redirect: true });
    }
  }

  submitFormHandler = e => {
    e.preventDefault();

    let childObj = {
      firstName: this.state.childFirstName,
      lastName: this.state.childLastName,
      gender: this.state.childGender,
      dob: this.state.childDob
    };
    ApiService.registerChild(childObj).then(response => {
      if (response.status === 201) {
        this.setState({
          childFirstName: "",
          childLastName: "",
          childGender: "",
          childDob: ""
        });
        this.setState({
          message: `Thank you for your submission.`
        });
      }
    });
  };
  changeHandler = e => {
    // console.log(e.target.value);
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    const fnameCamelcased = _.startCase(_.camelCase(this.state.firstName));
    const hasChild = this.state.child.length === 0;
    const { childFirstName, childLastName, childGender, childDob } = this.state;

    return (
      <div>
        <Modal show={this.state.showModal} onHide={this.closeHandler}>
          <Modal.Header closeButton>
            <Modal.Title>Child details</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          {this.state.message && (
              <div className="text-left pt-4 text-danger">
                {this.state.message}
                <br />
              </div>

            )}

            <form onSubmit={this.submitFormHandler}>
              <div className="form-group">
                <label htmlFor="text">Child FirstName</label>
                <input
                  type="text"
                  className="form-control"
                  id="text"
                  name="childFirstName"
                  onChange={this.changeHandler}
                  value={childFirstName}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="text">Child LastName</label>
                <input
                  type="text"
                  className="form-control"
                  id="text"
                  name="childLastName"
                  onChange={this.changeHandler}
                  value={childLastName}
                />
              </div>
              <select
                className="custom-select"
                name="childGender"
                onChange={this.changeHandler}
                value={childGender}
              >
                <option value>Gender</option>
                <option value="MALE">Boy</option>
                <option value="FEMALE">Girl</option>
              </select>
              <br />
              <br />
              <div className="form-group">
                <label htmlFor="date">Date Of Birth</label>
                <input
                  className="form-control"
                  type="date"
                  name="childDob"
                  value={childDob}
                  onChange={this.changeHandler}
                />
              </div>
              <br />
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
            </form>
          </Modal.Body>
        </Modal>
        <section className="section pt-4 pb-4 osahan-account-page">
          <div className="container">
            <div className="row">
              <div className="col-md-3">
                <div className="osahan-account-page-left shadow-sm bg-white h-100">
                  <div className="border-bottom p-4">
                    <div className="osahan-user text-center">
                      <div className="osahan-user-media">
                        <img
                          className="mb-3 rounded-pill shadow-sm mt-1"
                          src={require("../img/profile-avatar.png")}
                          alt={this.state.firstName}
                        />
                        <div className="osahan-user-media-body">
                          <h6 className="mb-2">{fnameCamelcased}</h6>
                          <p className="mb-1">{this.state.lastName}</p>
                          <p>{this.state.email}</p>
                          <p className="mb-0 text-black font-weight-bold">
                            <Link
                              className="text-primary mr-3"
                              data-toggle="modal"
                              data-target="#edit-profile-modal"
                              to=""
                            >
                              <i className="icofont-ui-edit" /> EDIT
                            </Link>
                          </p>
                        </div>
                        <hr />

                        <div className="osahan-user-media-body">
                          {this.state.child.length === 0}
                          <Alert color="info">
                            {fnameCamelcased}, you haven't entered child details
                            yet.
                            <br />
                            <Link to="#" onClick={this.openHandler}>
                              Let's do it now
                            </Link>
                          </Alert>
                        </div>
                      </div>
                    </div>
                  </div>
                  {/* Site item component */}
                  <SideMenu />
                </div>
              </div>
              <div className="col-md-6" id="contentId">
                <div className="osahan-account-page-right shadow-sm bg-white p-4 h-100">
                  <div className="tab-content" id="myTabContent">
                    <div
                      className="tab-pane fade show active"
                      id="orders"
                      role="tabpanel"
                      aria-labelledby="orders-tab"
                    >
                      {!hasChild ? <Welcome name={this.state.firstName} /> : ""}
                    </div>
                  </div>
                </div>
              </div>
              {/* Site item component */}
              <RightMenu />
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default Dashboard;
