import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect } from "react-router-dom";
import { SESSION_STORAGE_KEY_LOGIN } from "../constants/Constants";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false
    };
  }
  logoutHandler = e => {
    //console.log(e)
    ApiService.logout()
      .then(response => {
        sessionStorage.setItem(SESSION_STORAGE_KEY_LOGIN, "");
        //this.setState({ redirect: true });
        this.props.history.push("/")
      })
      .catch(error => {
        console.log(error);
      });
  };

  componentDidMount() {
    if (sessionStorage.getItem(SESSION_STORAGE_KEY_LOGIN)) {
      console.log(sessionStorage.getItem(SESSION_STORAGE_KEY_LOGIN));
    } else {
     // this.setState({ redirect: true });
    }
  }
  render() {
   // if (this.state.redirect) {
     // return <Redirect to={"/"} />;
   // }
    return (
      <div>
        <section className="breadcrumb-osahan pt-5 pb-5 bg-light position-relative text-center">
          <h1 className="text-white">Dashboard</h1>
          <h6 className="text-white-50">Dasbboard</h6>

          <button onClick={this.logoutHandler}>
            Logout 
          </button>
        </section>
      </div>
    );
  }
}

export default Dashboard;
