import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect } from "react-router-dom";
import { SESSION_STORAGE_KEY_LOGIN, UNAME } from "../constants/Constants";
import CryptoAES from "crypto-js/aes";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false
    };
  }
  logoutHandler = e => {
    sessionStorage.setItem(SESSION_STORAGE_KEY_LOGIN, "");
    sessionStorage.setItem(UNAME, "");
    this.setState({ redirect: true });
  };

  componentDidMount() {
    if (
      sessionStorage.getItem(SESSION_STORAGE_KEY_LOGIN) &&
      sessionStorage.getItem(UNAME)
    ) {
      const data = sessionStorage.getItem(UNAME);
      ApiService.getParentData(data)
        .then(response => {
          if (response.status === 200) {
            sessionStorage.setItem(
              SESSION_STORAGE_KEY_LOGIN,
              response.headers.authorization
            );
            let username = CryptoAES.encrypt(this.state.username, CIPHER_KEY);
            sessionStorage.setItem(UNAME, username);
            this.props.history.push("/dashboard");
          }
        })
        .catch(error => {});
    } else {
      this.setState({ redirect: true });
    }
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    return (
      <div>
        <section className="breadcrumb-osahan pt-5 pb-5 bg-light position-relative text-center">
          <h1 className="text-white">Dashboard</h1>
          <h6 className="text-white-50">Dasbboard</h6>

          <button onClick={this.logoutHandler}>Logout</button>
        </section>
      </div>
    );
  }
}

export default Dashboard;
