import axios from "axios";
const BASE_URL = process.env.REACT_APP_BASE_URL;

let headers = {
  "Access-Control-Allow-Origin": "POST"
};

class ApiService {
  createNewAccount(data) {
    return axios.post(`${BASE_URL}/public/addNew/parent`, data, {
      headers: headers
    });
  }
  login(data) {
    return axios.post(`${BASE_URL}/public/login`, data, {
      headers: headers
    });
  }
  logout() {
    return axios.get(`${BASE_URL}/public/logout`, {
      headers: headers
    });
  }
}

export default new ApiService();
