import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../css/App.css";
import ApiService from "../service/ApiService";

class Register extends Component {
  state = {
    firstName: "",
    lastName: "",
    username: "",
    password: "",
    email: ""
  };

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = e => {
    e.preventDefault();
    let message = document.createElement("span");
    message.textContent = "You are successfully registered. You may now login.";

    ApiService.createNewAccount(this.state)
      .then(response => {

        if (response.status === 201) {
          document.getElementById("msg").appendChild(message);
          this.setState({
            firstName: "",
            lastName: "",
            username: "",
            password: "",
            email: ""
          });
        } else {
          console.log("hello====", response.status)
        }
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    const { firstName, lastName, username, password, email } = this.state;
    return (
      <div>
        <div className="bg-white">
          <div className="container-fluid">
            <div className="row no-gutter">
              <div className="d-none d-md-flex col-md-5 col-lg-6 bg-image" />
              <div className="col-md-8 col-lg-6">
                <div className="login d-flex align-items-center py-5">
                  <div className="container">
                    <div className="row">
                      <div className="col-md-9 col-lg-8 mx-auto pl-5 pr-5">
                        <h3 className="login-heading mb-4">Create Account</h3>
                        <div className="text-left pt-3" id="msg" />
                        <form onSubmit={this.submitHandler}>
                          <div className="form-label-group">
                            <input
                              type="text"
                              name="firstName"
                              id="inputFirstName"
                              className="form-control"
                              placeholder="firstName"
                              value={firstName}
                              onChange={this.changeHandler}
                              required
                            />
                            <label htmlFor="inputFirstName">FirstName</label>
                          </div>
                          <div className="form-label-group">
                            <input
                              type="text"
                              name="lastName"
                              id="inputLasttName"
                              className="form-control"
                              placeholder="lastName"
                              value={lastName}
                              onChange={this.changeHandler}
                              required
                            />
                            <label htmlFor="inputLastName">LastName</label>
                          </div>
                          <div className="form-label-group">
                            <input
                              type="text"
                              name="username"
                              id="inputUsername"
                              className="form-control"
                              placeholder="username"
                              onChange={this.changeHandler}
                              value={username}
                              required
                            />
                            <label htmlFor="inputUsername">Username</label>
                          </div>
                          <div className="form-label-group">
                            <input
                              type="email"
                              name="email"
                              id="inputEmail"
                              className="form-control"
                              placeholder="Email address"
                              onChange={this.changeHandler}
                              value={email}
                              required
                            />
                            <label htmlFor="inputEmail">Email address</label>
                          </div>
                          <div className="form-label-group">
                            <input
                              type="password"
                              name="password"
                              id="inputPassword"
                              className="form-control"
                              placeholder="Password"
                              onChange={this.changeHandler}
                              value={password}
                              required
                            />
                            <label htmlFor="inputPassword">Password</label>
                          </div>
                          <button
                            type="submit"
                            className="btn btn-lg btn-outline-primary btn-block btn-login text-uppercase font-weight-bold mb-2"
                          >
                            Sign Up
                          </button>
                          <div className="text-center pt-3">
                            Already have an Account?{" "}
                            <Link className="font-weight-bold" to="/">
                              Sign In
                            </Link>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
