import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect } from "react-router-dom";
import {
  SESSION_STORAGE_TOKEN,
  SESSION_STORAGE_USERNAME
} from "../constants/Constants";
import "../css/App.css";
import SideItem from "./pageComponents/SideItem";
import Rightside from "./pageComponents/Rightside";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      redirect: false
    };
  }
  logoutHandler = e => {
    sessionStorage.removeItem(SESSION_STORAGE_TOKEN);
    sessionStorage.removeItem(SESSION_STORAGE_USERNAME);
    this.setState({
      firstName: "",
      lastName: "",
      email: "",
      redirect: true
    });
  };

  componentDidMount() {
    if (
      sessionStorage.getItem(SESSION_STORAGE_TOKEN) &&
      sessionStorage.getItem(SESSION_STORAGE_USERNAME)
    ) {
      const token = sessionStorage.getItem(SESSION_STORAGE_TOKEN);
      const data = sessionStorage.getItem(SESSION_STORAGE_USERNAME);

      console.log("username:", data);
      console.log("token", token);

      ApiService.getParentData(data).then(response => {
        this.setState({
          firstName: response.data.firstName,
          lastName: response.data.lastName,
          email: response.data.email
        });
      });
    } else {
      this.setState({ redirect: true });
    }
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    return (
      <div>
        <section className="section pt-4 pb-4 osahan-account-page">
          <div className="container">
            <div className="row">
              <div className="col-md-3">
                <div className="osahan-account-page-left shadow-sm bg-white h-100">
                  <div className="border-bottom p-4">
                    <div className="osahan-user text-center">
                      <div className="osahan-user-media">
                        <img
                          className="mb-3 rounded-pill shadow-sm mt-1"
                          src=""
                          alt={this.state.firstName}
                        />
                        <div className="osahan-user-media-body">
                          <h6 className="mb-2">{this.state.firstName}</h6>
                          <p className="mb-1">{this.state.lastName}</p>
                          <p>{this.state.email}</p>
                          <p className="mb-0 text-black font-weight-bold">
                            <a
                              className="text-primary mr-3"
                              data-toggle="modal"
                              data-target="#edit-profile-modal"
                              href="#"
                            >
                              <i className="icofont-ui-edit" /> EDIT
                            </a>
                          </p>
                        </div>
                      </div>
                    </div>
                  </div>
                  {/* Site item component */}
                  <SideItem />
                </div>
              </div>

              <div class="col-md-5">
                <div class="osahan-account-page-right shadow-sm bg-white p-4 h-100">
                  <div class="tab-content" id="myTabContent">
                    <div
                      class="tab-pane fade show active"
                      id="orders"
                      role="tabpanel"
                      aria-labelledby="orders-tab"
                    >
                      <h4 class="font-weight-bold mt-0 mb-4">Past Orders</h4>
                      <div class="bg-white card mb-4 order-list shadow-sm">
                        <div class="gold-members p-4">


                        <a href="#">
                            <div class="media">
                              <img class="mr-4" src="" alt="Generic placeholder image"/>
                              <div class="media-body">
                                <span class="float-right text-info">Delivered on Mon, Nov 12, 7:18 PM <i class="icofont-check-circled text-success"></i></span>
                                 <h6 class="mb-2">
                                     <a href="detail.html" class="text-black">Gus's World Famous Fried Chicken</a>
                                </h6>
                                </div>
                            </div>
                          </a>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              {/* Site item component */}
              <SideItem/>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default Dashboard;
