import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect, Link } from "react-router-dom";
import {
  SESSION_STORAGE_TOKEN,
  SESSION_STORAGE_USERNAME
} from "../constants/Constants";
import "../css/App.css";
import { SideMenu } from "./pageComponents/SideMenu";
import { RightMenu } from "./pageComponents/RightMenu";
import { ChildForm } from "./pageComponents/ChildForm";
import Welcome from "./pageComponents/Welcome";
import { Alert } from "reactstrap";
import _ from "lodash";
import { Modal, Button } from 'react-bootstrap';

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      child: [],
      redirect: false
    };
  }

  logoutHandler = e => {
    sessionStorage.removeItem(SESSION_STORAGE_TOKEN);
    sessionStorage.removeItem(SESSION_STORAGE_USERNAME);
    this.setState({
      firstName: "",
      lastName: "",
      email: "",
      child: [],
      redirect: true
    });
  };
  getInitialState = () => {
    return { showModal: false };
  }

  closeHandler = () => {
    this.setState({ showModal: false });
  }

  openHandler = () =>{
    this.setState({ showModal: true });
  }

  componentDidMount() {
    if (
      sessionStorage.getItem(SESSION_STORAGE_TOKEN) &&
      sessionStorage.getItem(SESSION_STORAGE_USERNAME)
    ) {
      const token = sessionStorage.getItem(SESSION_STORAGE_TOKEN);
      const data = sessionStorage.getItem(SESSION_STORAGE_USERNAME);

      console.log("username:", data);
      console.log("token", token);

      ApiService.getParentData(data).then(response => {
        this.setState({
          firstName: response.data.firstName,
          lastName: response.data.lastName,
          email: response.data.email,
          child: response.data.child
        });
      });
    } else {
      this.setState({ redirect: true });
    }
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    const fnameCamelcased = _.startCase(_.camelCase(this.state.firstName));
    const hasChild = this.state.child.length === 0;

    return (
      <div>
        <Modal show={this.state.showModal} onHide={this.closeHandler}>
          <Modal.Header closeButton>
            <Modal.Title><Alert color="info">Please enter your child details</Alert></Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>Text in a modal</h4>
            <p>Duis mollis, est non commodo luctus, nisi erat porttitor ligula.</p>
            <h4>Popover in a modal</h4>
            <hr />
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={this.closeHandler}>Close</Button>
          </Modal.Footer>
        </Modal>
        <section className="section pt-4 pb-4 osahan-account-page">
          <div className="container">

            <div className="row">
              <div className="col-md-3">
                <div className="osahan-account-page-left shadow-sm bg-white h-100">
                  <div className="border-bottom p-4">

                    <div className="osahan-user text-center">
                      <div className="osahan-user-media">
                        <img
                          className="mb-3 rounded-pill shadow-sm mt-1"
                          src={require("../img/profile-avatar.png")}
                          alt={this.state.firstName}
                        />
                        <div className="osahan-user-media-body">
                          <h6 className="mb-2">{fnameCamelcased}</h6>
                          <p className="mb-1">{this.state.lastName}</p>
                          <p>{this.state.email}</p>
                          <p className="mb-0 text-black font-weight-bold">
                            <a
                              className="text-primary mr-3"
                              data-toggle="modal"
                              data-target="#edit-profile-modal"
                              href="#"
                            >
                              <i className="icofont-ui-edit" /> EDIT
                            </a>
                          </p>
                        </div>
                        <hr />

                        <div className="osahan-user-media-body">
                          <Alert color="info">
                            {this.state.child.length === 0}
                            {fnameCamelcased}, you haven't entered child details
                            yet.
                            <br />
                            <Link to="#" onClick={this.openHandler}>Let's do it now</Link>
                          </Alert>
                        </div>
                      </div>
                    </div>
                  </div>
                  {/* Site item component */}
                  <SideMenu />
                </div>
              </div>
              <div class="col-md-6" id="contentId">

                <div class="osahan-account-page-right shadow-sm bg-white p-4 h-100">
                  <div class="tab-content" id="myTabContent">
                   <div
                      class="tab-pane fade show active"
                      id="orders"
                      role="tabpanel"
                      aria-labelledby="orders-tab"
                    >

                      {!hasChild ? <Welcome name={this.state.firstName} /> : ""}
                    </div>
                  </div>
                </div>
              </div>
              {/* Site item component */}
              <RightMenu />
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default Dashboard;
