import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect } from "react-router-dom";
import { SESSION_STORAGE_KEY_LOGIN } from "../constants/Constants";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false
    };
  }
  logoutHandler = e => {
    ApiService.logout()
      .then(response => {
        console.log(response)
        sessionStorage.setItem(SESSION_STORAGE_KEY_LOGIN, "");
        this.setState({ redirect: true });
      })
      .catch(error => {
        console.log(error);
      });
  };

  componentWillMount() {
    let item = sessionStorage.getItem(SESSION_STORAGE_KEY_LOGIN);
    console.log("session",item);
    if (item == null) {
      this.setState({ redirect: true });
    }
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    return (
      <div>
        <section className="breadcrumb-osahan pt-5 pb-5 bg-light position-relative text-center">
          <h1 className="text-white">Dashboard</h1>
          <h6 className="text-white-50">Dasbboard</h6>
          <a href="#" onClick={this.logoutHandler}>
            Logout
          </a>
        </section>
      </div>
    );
  }
}

export default Dashboard;
