import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import _ from "lodash";
import { Modal } from "react-bootstrap";
class ChildModal extends Component {
  closeHandler = () => {
    if(this.props.showModal == true) {
      this.props.showModal = false;
    }
  };



  render() {
   const showModal  = this.props.showModal;

    return (
      <Modal show={showModal} onHide={this.closeHandler}>
        <Modal.Header closeButton>
          <Modal.Title>Child details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {this.props.message && (
            <div className="text-left pt-4 text-danger">
              {this.props.message}
              <br />
            </div>
          )}

          <form onSubmit={this.submitFormHandler}>
            <div className="form-group">
              <label htmlFor="text">Child FirstName</label>
              <input
                type="text"
                className="form-control"
                id="text"
                name="childFirstName"
                onChange={this.changeHandler}
                value={this.props.childFirstName}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="text">Child LastName</label>
              <input
                type="text"
                className="form-control"
                id="text"
                name="childLastName"
                onChange={this.changeHandler}
                value={this.props.childLastName}
              />
            </div>
            <select
              className="custom-select"
              name="childGender"
              onChange={this.changeHandler}
              value={this.props.childGender}
            >
              <option value>Gender</option>
              <option value="MALE">Boy</option>
              <option value="FEMALE">Girl</option>
            </select>
            <br />
            <br />
            <div className="form-group">
              <label htmlFor="date">Date Of Birth</label>
              <input
                className="form-control"
                type="date"
                name="childDob"
                value={this.props.childDob}
                onChange={this.changeHandler}
              />
            </div>
            <br />
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </Modal.Body>
      </Modal>
    );
  }
}
export default ChildModal;
