import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect } from "react-router-dom";
import {
  SESSION_STORAGE_TOKEN,
  SESSION_STORAGE_USERNAME
} from "../constants/Constants";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      redirect: false
    };
  }
  logoutHandler = e => {
    sessionStorage.removeItem(SESSION_STORAGE_TOKEN);
    sessionStorage.removeItem(SESSION_STORAGE_USERNAME);
    this.setState({
      firstName: "",
      lastName: "",
      email: "",
      redirect: true
    });
  };

  componentDidMount() {
    if (
      sessionStorage.getItem(SESSION_STORAGE_TOKEN) &&
      sessionStorage.getItem(SESSION_STORAGE_USERNAME)
    ) {
      const token = sessionStorage.getItem(SESSION_STORAGE_TOKEN);
      const data = sessionStorage.getItem(SESSION_STORAGE_USERNAME);

      console.log("username:", data);
      console.log("token", token);

      ApiService.getParentData(data).then(response => {
        this.setState({
          firstName: response.data.firstName,
          lastName: response.data.lastName,
          email: response.data.email
        });
      });
    } else {
      this.setState({ redirect: true });
    }
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    return (
      <div>
        <aside className="customizer">
          <a href="javascript:void(0)" className="service-panel-toggle">
            <i className="fa fa-spin fa-cog" />
          </a>
          <div
            className="customizer-body ps-container ps-theme-default ps-active-y"
            data-ps-id="2577acba-c1c3-74e7-5972-c192befaf14c"
          >
            <ul className="nav customizer-tab" role="tablist">
              <li className="nav-item">
                <a
                  className="nav-link active"
                  id="pills-home-tab"
                  data-toggle="pill"
                  href="#pills-home"
                  role="tab"
                  aria-controls="pills-home"
                  aria-selected="true"
                >
                  <i className="mdi mdi-wrench font-20" />
                </a>
              </li>
              <li className="nav-item">
                <a
                  className="nav-link"
                  id="pills-profile-tab"
                  data-toggle="pill"
                  href="#chat"
                  role="tab"
                  aria-controls="chat"
                  aria-selected="false"
                >
                  <i className="mdi mdi-message-reply font-20" />
                </a>
              </li>
              <li className="nav-item">
                <a
                  className="nav-link"
                  id="pills-contact-tab"
                  data-toggle="pill"
                  href="#pills-contact"
                  role="tab"
                  aria-controls="pills-contact"
                  aria-selected="false"
                >
                  <i className="mdi mdi-star-circle font-20" />
                </a>
              </li>
            </ul>
          </div>
        </aside>
        <section className="breadcrumb-osahan pt-5 pb-5 bg-light position-relative text-center">
          <h1 className="text-white">Dashboard</h1>
          <h6 className="text-white-50">Dasbboard</h6>
          <h1>
            {this.state.firstName} {this.state.lastName}{" "}
          </h1>

          <button onClick={this.logoutHandler}>Logout</button>
        </section>
      </div>
    );
  }
}

export default Dashboard;
