import React, { Component } from "react";
import "../css/App.css";
import ApiService from "../service/ApiService";
import { Redirect } from "react-router-dom";
import {
  SESSION_STORAGE_TOKEN,
  SESSION_STORAGE_USERNAME,
  CIPHER_KEY
} from "../constants/Constants";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      redirect: false
    };
  }
  logoutHandler = e => {
    sessionStorage.setItem(SESSION_STORAGE_TOKEN, "");
    sessionStorage.setItem(SESSION_STORAGE_USERNAME, "");
    this.setState({ redirect: true });
  };

  componentDidMount() {
    if (
      sessionStorage.getItem(SESSION_STORAGE_TOKEN) &&
      sessionStorage.getItem(SESSION_STORAGE_USERNAME)
    ) {
      const token = sessionStorage.getItem(SESSION_STORAGE_TOKEN);
      const data = sessionStorage.getItem(SESSION_STORAGE_USERNAME);

      console.log("username:", data);
      console.log("token", token);

      ApiService.getParentData(data).then(response => {
        console.log(response);
        this.setState({
          firstName: response.data.firstName,
          lastName: response.data.lastName,
          email: response.data.email
        });
      });
    } else {
      this.setState({ redirect: true });
    }
  }
  render() {
    if (this.state.redirect) {
      return <Redirect to={"/"} />;
    }
    return (
      <div>
        <section className="breadcrumb-osahan pt-5 pb-5 bg-light position-relative text-center">
          <h1 className="text-white">Dashboard</h1>
          <h6 className="text-white-50">Dasbboard</h6>
          <h1>
            {this.state.firstName} {this.state.lastName}{" "}
          </h1>

          <button onClick={this.logoutHandler}>Logout</button>
        </section>
      </div>
    );
  }
}

export default Dashboard;
